/**
 * Upload an IBT file to generate and store a track boundary.

The IBT file should contain at least two laps:
- One lap hugging the left side of the track
- One lap hugging the right side of the track

If a boundary already exists for the track+config, it will be replaced.

Defaults follow the Garage61 collection method:
- Left boundary: Lap 1 (after reset at start line)
- Right boundary: Lap 3 (after reset at start line)
 * @summary Upload Track Boundary
 */
export const uploadTrackBoundary = (
  bodyUploadTrackBoundary: BodyType<BodyUploadTrackBoundary>,
  options?: SecondParameter<typeof customInstance>,
  signal?: AbortSignal,
) => {
  const formData = new FormData();
  formData.append(`file`, bodyUploadTrackBoundary.file);
  if (bodyUploadTrackBoundary.left_lap_number !== undefined) {
    formData.append(
      `left_lap_number`,
      bodyUploadTrackBoundary.left_lap_number.toString(),
    );
  }
  if (bodyUploadTrackBoundary.right_lap_number !== undefined) {
    formData.append(
      `right_lap_number`,
      bodyUploadTrackBoundary.right_lap_number.toString(),
    );
  }

  return customInstance<TrackBoundaryUploadResponse>(
    {
      url: `/api/v1/tracks/upload`,
      method: "POST",
      headers: { "Content-Type": "multipart/form-data" },
      data: formData,
      signal,
    },
    options,
  );
};

